mapper.insert:
/* KuderUserMapper.insertUser : 사용자정보 추가 */
INSERT INTO kuder_user (
	user_email
	, user_password
	, user_name
	, user_phone
	, authority_code
	, regist_date
) VALUES (
	#{userEmail}
	, #{userPassword}
	, #{userName}
	, #{userPhone}
	, #{authorityCode}
	, DATE_FORMAT(NOW(), '%y-%m-%d %T')
)

	/* KuderUserMapper.insertActivationCode : activation코드 등록 */
	INSERT INTO kuder_activation_code (
		activation_code
		, total_count
		, able_count
		, unable_count
		, product_name
		, service_start_date
		, service_end_date
		, user_type
		, bb_group_code
		, user_email
		, use_type
	) VALUES (
		#{activationCode}
		, #{totalCount}
		, #{ableCount}
		, 0
		, #{productName}
		, DATE_FORMAT(NOW(), '%y-%m-%d %T')
		, DATE_FORMAT(date_add(NOW(), interval + 12 month), '%y-%m-%d %T')
		, #{userType}
		
			
				, null
			
			
				, #{bbGroupCode}
			
		
		, #{userEmail}
		, 'Y'
	)

/* KuderUserMapper.insertTinNum : tin번호 추가 */
SELECT tin_number FROM kuder_assessment WHERE activation_code = #{activationCode} AND user_email = #{userEmail}
INSERT INTO kuder_assessment ( tin_number , user_email , product_name , assessment_count , assessment_YN , service_start_date , service_end_date , activation_code , use_type ) VALUES ( CONCAT(#{activationCode}, '_', (SELECT unable_count FROM kuder_activation_code WHERE activation_code = #{activationCode})) , #{userEmail} , (SELECT product_name FROM kuder_activation_code WHERE activation_code = #{activationCode}) , 0 , 'N' , DATE_FORMAT(NOW(), '%y-%m-%d %T')
, (SELECT service_end_date FROM kuder_activation_code WHERE activation_code = #{activationCode}) , #{activationCode} , 'Y' )

mapper.update:
/* UserMapper.updateUser : 사용자정보 수정 */
UPDATE 
	kuder_user
SET
	user_name = #{userName}
	, user_phone = #{userPhone}
	, update_date = DATE_FORMAT(NOW(), '%y-%m-%d %T')
WHERE
	user_email = #{userEmail}
AND
	user_id = #{userId}
